CREATE TABLE "lend_a_book"."invitation_acceptance" ("id" serial NOT NULL, "invitation" integer NOT NULL, "accepted_by" integer NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("invitation") REFERENCES "lend_a_book"."invitation"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("accepted_by") REFERENCES "lend_a_book"."user"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("invitation", "accepted_by"));COMMENT ON TABLE "lend_a_book"."invitation_acceptance" IS E'User Accepting an invitation is added here, because users usually can abuse the invitation, one person can only join using an invitation just once else needs a new invitation.';
